import React from "react";
import { useEffect, useState, createElement } from "react";
import { useParams } from "react-router-dom/cjs/react-router-dom.min";
import { Switch, Route, Link } from "react-router-dom";
import axios from "axios";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";

const p = (a) => {
  console.log(a);
};
const About = (props) => {
  const [deploymentItem, setDeploymentItem] = useState([
    {
      title: "AWS EC2",
      about: "A cloud-based, scalable computer.",
      link: "https://aws.amazon.com/",
    },
    {
      title: "Ubuntu",
      about: "A popular linux distro based on Debian.",
      link: "https://ubuntu.com/",
    },
    {
      title: "SSH",
      about: "Easy file operations using .ppk & .pem.",
      link: "",
    },
  ]);
  const [frontEndItem, setFrontEndItem] = useState([
    {
      title: "Create-React-App",
      about: "Facebook's JavaScript library and configured webpack for building UI.",
      link: "https://create-react-app.dev/",
    },
    {
      title: "Bootstrap",
      about: "The worldâ€™s most popular front-end open source toolkit.",
      link: "https://getbootstrap.com/",
    },
    {
      title: "CSS",
      about: "Front-end display and style.",
      link: "https://getbootstrap.com/",
    },
    {
      title: "Google Fonts",
      about:
        "a web font service owned by Google. This includes free and open source font families, an interactive web directory for browsing the library, and APIs for using the fonts via CSS.",
      link: "https://fonts.google.com//",
    },
  ]);
  const [backEndItem, setBackEndItem] = useState([
    {
      title: "Mongo DB",
      about: "A non-relational database that plays well with json. ",
      link: "",
    },
    {
      title: "Mongoose",
      about: "A database helper",
      link: "",
    },
    {
      title: "Express",
      about: "x. ",
      link: "",
    },
    {
      title: "Cors",
      about: "x. ",
      link: "",
    },
    {
      title: "Nodemon",
      about: "x. ",
      link: "",
    },
    {
      title: "Nginx",
      about:
        "A web server that can also be used as a reverse proxy, load balancer, mail proxy and HTTP cache. ",
      link: "https://www.nginx.com/",
    },
  ]);

  return (
    <>
      <div className="box">
        <Link to={"/"}>
          <button className="btn btn-secondary">
            <big>Home</big>
          </button>
        </Link>
        <h2>About This Project</h2>
      </div>

      <div className="box0">
        <div className="lr">
          <h4>Front End</h4>
          <table className="table table-sm table-hover my-3">
            <tbody>
              {frontEndItem.map((item, i) => {
                return (
                  <tr>
                    <td>
                      <a className="d-grid" target="_blank" href={item.link}>
                        <button className="btn btn-pri">
                          <h5 className="align-middle">{item.title}</h5>
                        </button>
                      </a>
                    </td>
                    <td className="text-start align-middle">{item.about}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
        <div className="lr">
          <h4>Back End</h4>
          <table className="table table-sm table-hover my-3">
            <tbody>
              {backEndItem.map((item, i) => {
                return (
                  <tr>
                    <td>
                      <a className="d-grid" target="_blank" href={item.link}>
                        <button className="btn btn-warning">
                          <h5 className="align-middle">{item.title}</h5>
                        </button>
                      </a>
                    </td>
                    <td className="text-start align-middle">{item.about}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
        <div className="lr">
          <h4>Deployment</h4>
          <table className="table table-sm table-hover my-3">
            <tbody>
              {deploymentItem.map((item, i) => {
                return (
                  <tr>
                  <td>
                    <a className="d-grid" target="_blank" href={item.link}>
                      <button className="btn btn-success">
                        <h5 className="align-middle">{item.title}</h5>
                      </button>
                    </a>
                  </td>
                  <td className="text-start align-middle">{item.about}</td>
                </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default About;
