import React from "react";
import { useEffect, useState, createElement } from "react";
import { useParams } from "react-router-dom/cjs/react-router-dom.min";
import { Switch, Route, Link } from "react-router-dom";
import axios from "axios";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";

require("../demo.gif");

const p = (a) => {
    console.log(a);
};
const Add = (props) => {
    const { _id } = useParams();
    // const [one, setOne] = useState({
    //   name: "default",

    // });
    const [form, setForm] = useState({
        pirateName: "",
        imageUrl: "",
        numChests: 0,
        catchPhrase: "",
        crewPosition: "",
        pegLeg: false,
        eyePatch: false,
        hookHand: false,
    });
    // const [error, setError] = useState({ name: {} });
    const [error, setError] = useState({
        // pirateName: "init",
        // imageUrl: "init",
        // numChests: 0,
        // catchPhrase: "init",
        // crewPosition: "init",
        // pegLeg: false,
        // eyePatch: false,
        // hookHand: false,
    });
    const [react_error, set_react_error] = useState({
        pirateName: "init",
        imageUrl: "init",
        numChests: "inint",
        catchPhrase: "init",
        crewPosition: "init",
        pegLeg: "init",
        eyePatch: "init",
        hookHand: "init",
    });

    const history = useHistory();

    const onSubmitHandler = (event) => {
        p("Running onSubmitHandler");
        event.preventDefault();

        axios
            .post("http://localhost:9000/api/pirate/create", form)
            .then((res) => {
                console.log(res.data);
                history.push(`/`);
            })
            .catch((err) => {
                p("in OnSubmitHandler Error");
                console.log(err.response.data.error.errors);
                setError(err.response.data.error.errors);
            });
    };

    const onChangeHandler = (event) => {
        // event.preventDefault();
        console.log("On change handler");
        // p(event.target.value);
        const newState = {
            ...form,
            [event.target.name]: event.target.value,
        };
        setForm(newState);

        //////////// front-end errors

        var newErrorValue = "";

        switch (event.target.name) {
            case "pirateName":
                console.log("pirateName");
                console.log(event.target.value.length);
                newErrorValue =
                    event.target.value.length < 3
                        ? "Pirate Name must be at least 3 characters"
                        : event.target.value.length > 16
                        ? "Pirate Name can't be more than 16 characters"
                        : "";
                break;
            case "imageUrl":
                console.log("imageUrl");
                console.log(event.target.value.length);
                newErrorValue =
                    event.target.value.length < 3
                        ? "Image URL must be at least 3 characters"
                        : "";

                break;
            case "catchPhrase":
                console.log("catchPhrase");
                console.log(event.target.value.length);
                newErrorValue =
